import React, { useEffect, useState } from 'react'
import { addDays, startOfDay, format } from 'date-fns'
import { Stashpoint, DateRange } from './Data'

type DraftCart = {
  stashpointId: any
  dateRange: { from: Date; to: Date }
  bagCount: number
}

const getInitialDraftCart = (): DraftCart => {
  const initialDateFrom = addDays(startOfDay(new Date()), 1)

  return {
    bagCount: 1,
    dateRange: { from: initialDateFrom, to: addDays(initialDateFrom, 1) },
    stashpointId: undefined,
  }
}

const Home = () => {
  const [stachList, setStachList] = useState<Stashpoint[]>()
  const [cart, setCart] = useState<DraftCart>(getInitialDraftCart)
  useEffect(() => {
    fetch('http://localhost:3000/api/stashpoints')
      .then((res) => res.json())
      .then((response: Stashpoint[]) => {
        console.log(response)
        setStachList(response)
      })
      .catch((err) => console.log(err))
  }, [])

  const handleChangeData = (e: any) => {
    console.log(e)
    let type: string = e.target.name
    let value: any = e.target.value
    let carts: DraftCart = { ...cart }
    if (value && value.length > 0) {
      switch (type) {
        case 'stashpoint':
          carts.stashpointId = value
          setCart(carts)
          break
        case 'fromDate':
          carts.dateRange.from = value
          setCart(carts)

          break
        case 'toDate':
          carts.dateRange.to = value
          setCart(carts)

          break
        case 'bagCount':
          carts.bagCount = parseInt(value)
          setCart(carts)

          break

        default:
          break
      }
      if (
        carts.bagCount &&
        carts.dateRange.from &&
        carts.dateRange.to &&
        carts.stashpointId
      ) {
        console.log('response here')

        fetch('http://localhost:3000/api/quotes', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(carts),
        })
          .then((res) => res.json())
          .then((response: Stashpoint[]) => {
            console.log('response', response)
          })
          .catch((err) => console.log(err))
      }
    }
  }

  console.log('cart', cart)
  return (
    <div>
      <h1>Home</h1>
      <br />
      <div>
        <select
          name='stashpoint'
          onChange={handleChangeData}
          value={cart.stashpointId}
        >
          <option></option>
          {stachList?.map((data) => {
            return (
              <option key={data.id} value={data.id}>
                {data.name}
              </option>
            )
          })}
        </select>
        <input
          type='date'
          className='fromDate'
          name='fromDate'
          min={format(new Date(cart.dateRange.from), 'yyyy-MM-dd')}
          onChange={handleChangeData}
        />
        <input
          type='date'
          min={format(new Date(cart.dateRange.to), 'yyyy-MM-dd')}
          className='toDate'
          name='toDate'
          onChange={handleChangeData}
        />
        <input
          type='number'
          name='bagCount'
          value={cart.bagCount}
          onInput={handleChangeData}
          min={1}
          max={50}
        />
        <button>Submit</button>
      </div>
    </div>
  )
}

export default Home
