import React, { useEffect, useState } from 'react'
import { addDays, startOfDay, format } from 'date-fns'
import { isDateRangeValid } from './util'
import { Stashpoint, BookingEncoded, PriceQuote } from './Data'

type DraftCart = {
  stashpointId: any
  dateRange: { from: Date; to: Date }
  bagCount: number
}

const getInitialDraftCart = (): DraftCart => {
  const initialDateFrom = addDays(startOfDay(new Date()), 1)

  return {
    bagCount: 1,
    dateRange: { from: initialDateFrom, to: addDays(initialDateFrom, 1) },
    stashpointId: undefined,
  }
}

const Home = () => {
  const [stachList, setStachList] = useState<Stashpoint[]>()
  const [cart, setCart] = useState<DraftCart>(getInitialDraftCart)
  const [priceQuote, setPriceQuote] = useState<PriceQuote>()
  const [booking, setBooking] = useState<BookingEncoded>()
  const [bookingId, setBookingId] = useState<number>()
  const [loading, setLoading] = useState<boolean>(false)
  useEffect(() => {
    fetch('http://localhost:3000/api/stashpoints')
      .then((res) => res.json())
      .then((response: Stashpoint[]) => {
        console.log(response)
        setStachList(response)
      })
      .catch((err) => console.log(err))
  }, [])

  const handleChangeData = (e: any) => {
    console.log(e)
    let type: string = e.target.name
    let value: any = e.target.value
    let carts: DraftCart = { ...cart }
    if (value && value.length > 0) {
      switch (type) {
        case 'stashpoint':
          carts.stashpointId = value
          setCart(carts)
          break
        case 'fromDate':
          carts.dateRange.from = new Date(value)
          setCart(carts)

          break
        case 'toDate':
          carts.dateRange.to = new Date(value)
          setCart(carts)

          break
        case 'bagCount':
          carts.bagCount = parseInt(value)
          setCart(carts)

          break

        default:
          break
      }
      if (
        carts.bagCount &&
        carts.dateRange.from &&
        carts.dateRange.to &&
        carts.stashpointId &&
        isDateRangeValid(carts.dateRange)
      ) {
        console.log('response here', carts.dateRange)
        carts.dateRange.from = startOfDay(new Date(carts.dateRange.from))

        carts.dateRange.to = startOfDay(new Date(carts.dateRange.to))
        setLoading(true)
        setBookingId(undefined)
        fetch('http://localhost:3000/api/quotes', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify(carts),
        })
          .then((res) => res.json())
          .then((response: PriceQuote) => {
            setLoading(false)

            setPriceQuote(response)
          })
          .catch((err) => console.log(err))
      }
    }
  }

  const payBooking = () => {
    setLoading(true)

    fetch('http://localhost:3000/api/bookings', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(priceQuote),
    })
      .then((res) => res.json())
      .then((response: BookingEncoded) => {
        setBooking(response)

        fetch('http://localhost:3000/api/payments', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ bookingId: response.id }),
        })
          .then((res) => res.json())
          .then((response: any) => {
            setLoading(false)

            console.log('booking', response)
            setBookingId(response.id)
            setPriceQuote(undefined)
            setCart(getInitialDraftCart)
          })
      })
      .catch((err) => console.log(err))
  }

  console.log('cart', cart, getInitialDraftCart().dateRange)
  console.log('priceQuote', priceQuote)

  if (loading) {
    return <span>Loading stashpoints&hellip;</span>
  }

  return (
    <div>
      <h1>Home</h1>
      <br />
      <div>
        <select
          name='stashpoint'
          onChange={handleChangeData}
          value={cart.stashpointId}
        >
          <option></option>
          {stachList?.map((data) => {
            return (
              <option
                key={data.id}
                value={data.id}
                selected={cart.stashpointId == data.id ? true : false}
              >
                {data.name}
              </option>
            )
          })}
        </select>
        <input
          type='date'
          className='fromDate'
          name='fromDate'
          //   min={format(new Date(cart.dateRange.from), 'yyyy-MM-dd')}
          onChange={handleChangeData}
          value={format(cart.dateRange.from, 'yyyy-MM-dd')}
        />
        <input
          type='date'
          //   min={format(new Date(cart.dateRange.to), 'yyyy-MM-dd')}
          className='toDate'
          name='toDate'
          onChange={handleChangeData}
          value={format(cart.dateRange.to, 'yyyy-MM-dd')}
        />
        <input
          type='number'
          name='bagCount'
          value={cart.bagCount}
          onInput={handleChangeData}
          min={1}
          max={50}
        />
      </div>
      <br />
      {priceQuote ? (
        <>
          <div>
            <table style={{ border: '1px solid' }}>
              <tr style={{ border: '1px solid' }}>
                <th>Stash Point</th>
                <th>Bag Count</th>
                <th>Currency Code</th>
                <th>From Date</th>
                <th>To Date</th>
                <th>Total Price</th>
              </tr>
              <tr>
                <td>
                  {
                    stachList?.find(
                      (data) => data.id == priceQuote.stashpointId,
                    )?.name
                  }
                </td>
                <td>{priceQuote.bagCount}</td>
                <td>{priceQuote.currencyCode}</td>
                <td>
                  {format(new Date(priceQuote.dateRange.from), 'yyyy-MM-dd')}
                </td>
                <td>
                  {format(new Date(priceQuote.dateRange.to), 'yyyy-MM-dd')}
                </td>
                <td>{priceQuote.totalPrice}</td>
              </tr>
            </table>
            <button onClick={payBooking}>Pay</button>
          </div>
        </>
      ) : null}
      {bookingId ? (
        <>
          <div>
            <h4>Booking {bookingId} successful</h4>
          </div>
        </>
      ) : null}
    </div>
  )
}

export default Home
